package day2;

public class ForewachLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {10,20,30,40,50};
		
		//for each
		for(int i: a)
		{
			System.out.println(i);
		}

		char ch[]= {'J','a','v','a'};
		
		for(char c:ch)
		{
			System.out.print(c);
		}
	}

}
